UPDATE quest_template SET RequestItemsText='Good day, $n. What have you got there?' WHERE entry=14160;
UPDATE quest_template SET OfferRewardText='This is a fine list of deeds, $n! You are to be rewarded handsomely for your accomplishments.' WHERE entry=14160;
UPDATE quest_template SET RequestItemsText='It\'s good to see you, $n! Do you have news from the front?' WHERE entry=14163;
UPDATE quest_template SET OfferRewardText='The Horde will never be a match for our forces as long as we have people like you on our side, $n.' WHERE entry=14163;
