INSERT IGNORE INTO npc_text (ID, text0_0, text0_1, prob0, lang0, em0_0, em0_1) VALUES ('14544','During the Day of the Dead, people gather in graveyards to celebrate with and cherish the spirits of those they have lost.$B$BYou can find the festivities in the cemeteries of any major city, where you\'ll be able to cook, dance, don costumes, and more.','During the Day of the Dead, people gather in graveyards to celebrate with and cherish the spirits of those they have lost.$B$BYou can find the festivities in the cemeteries of any major city, where you\'ll be able to cook, dance, don costumes, and more.','1','0','0','396');
