INSERT INTO page_text VALUES ('3151','<HTML>\r\n<BODY>\r\n<P>Admiral Barean Westwind showed up on my doorstep tonight! By all accounts he\'d died upon these shores an age ago.</P>\r\n<BR/>\r\n<P>He didn\'t look old enough and yet I knew that it was him. He claimed that he was the only survivor of his doomed fleet and that he\'d survived only through the good graces of the Light.</P>\r\n</BODY>\r\n</HTML>', '3152');
