INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1392, '尼尔斯修士：$B$B 我的葡萄得救了!$N，也就是这封信的携带者，勇敢地闯入我那满是盗贼的园子，然后带着我的葡萄杀了出来。虽然迪菲亚兄弟会的人依然在河岸上巡逻，但是我所有的收成几乎全都保住了。$B$B 你可以在你的商店中再次供应葡萄了。$B$B 愿圣光保佑我们，$B - 米莉·奥斯沃斯');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1430, '尼尔斯修士：$B$B 我的葡萄得救了!$N，也就是这封信的携带者，勇敢地闯入我那满是盗贼的园子，然后带着我的葡萄杀了出来。虽然迪菲亚兄弟会的人依然在河岸上巡逻，但是我所有的收成几乎全都保住了。$B$B 你可以在你的商店中再次供应葡萄了。$B$B 愿圣光保佑我们，$B - 米莉·奥斯沃斯');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1431, '沃弗克·铁壶$B$B 沃弗克在黑铁酒窖的储藏室工作，他为黑暗守护者做出的牺牲意义重大。$B$B 愿他的灵魂坚定而顽强。');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1432, '比塞克·雷眉$B$B 比塞克的游荡将他引入了黑石深渊，山脉的秘密在召唤着他。$B$B 愿他的灵魂永远不要踌躇。');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1433, '尤格尔·锤手$B$B 尤格尔是一位技巧高超的凿工，当他还没有遇到黑暗之钥的时候，他在工匠大厅中雕琢伟大的作品。');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1434, '希姆雷尔·暗齿$B$B 当黑暗之钥的疯狂占据希姆雷尔时，只有死者的尖叫可以使他平静。$B$B 希姆雷尔总是在竞技场旁坐着。愿我们的血腥搏杀可以平息他心中的怒气。');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1435, '奥弗加特·石拳$B$B 丧命于黑暗守护者奥弗加特锤下的挑战者不计其数。$B$B 西部兵营的士兵们在他走过时卑躬屈膝。他们之中有谁敢直视奥弗加特的双眼并看到黑暗之钥的秘密呢?');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1436, '佩沃尔·铁足$B$B 黑暗守护者佩沃尔是我们最为尊重的信徒。他携带黑暗之钥的时间远远超过其他黑暗守护者，这使他付出了惊人的代价。$B$B 他的牺牲让我们感到惋惜，对他的怀念会一直留在我们心中……自从黑暗占据了他。');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1470, '佩沃尔·铁足$B$B 黑暗守护者佩沃尔是我们最为尊重的信徒。他携带黑暗之钥的时间远远超过其他黑暗守护者，这使他付出了惊人的代价。$B$B 他的牺牲让我们感到惋惜，对他的怀念会一直留在我们心中……自从黑暗占据了他。');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1471, '所罗门镇长：$B$B这封命令的携带者已经被授予暴风城官方代表的资格。您可以让$g他:她;寻找黑龙和黑石兽人之间的联系。如果发现了此类证据，请让这个代表把证据带回暴风城，亲自交给我，届时我再酌情派遣部队来协助湖畔镇。$B$B此致，$B$B$B$B伯瓦尔·弗塔根公爵');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1490, '所罗门镇长：$B$B这封命令的携带者已经被授予暴风城官方代表的资格。您可以让$g他:她;寻找黑龙和黑石兽人之间的联系。如果发现了此类证据，请让这个代表把证据带回暴风城，亲自交给我，届时我再酌情派遣部队来协助湖畔镇。$B$B此致，$B$B$B$B伯瓦尔·弗塔根公爵');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1491, '<HTML><BR/> <BODY><BR/> <H1 align=\"center\"><BR/> 水晶塔使用手册$B </H1><BR/> <BR/><BR/> <P align=\"center\"> “安戈洛环形山水晶收集与使用手册”</P><BR/> <BR/><BR/> <BR/><BR/> <P align=\"left\">第一章：北部水晶塔 </P><BR/> <BR/><BR/> <P align=\"left\">第二章：东部水晶塔 </P><BR/> <BR/><BR/> <P align=\"left\">第三章：西部水晶塔 </P><BR/> <BR/><BR/> </BODY><BR/> </HTML>');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1510, '<HTML>$B<BODY>$B<H1 align=\"center\">$B水晶塔使用手册$B</H1>$B<BR/>$B<P align=\"center\"> “安戈洛环形山水晶收集与使用手册”</P>$B<BR/>$B<BR/>$B<P align=\"left\">第一章:北部水晶塔 </P>$B<BR/>$B<P align=\"left\">第二章:东部水晶塔 </P>$B<BR/>$B<P align=\"left\">第三章:西部水晶塔 </P>$B<BR/>$B</BODY>$B</HTML>');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1511, '<HTML><BR/> <BODY><BR/> <H1 align=\"center\"><BR/> 第一章：北部水晶塔<BR/> </H1><BR/> <BR/><BR/> <BR/><BR/> <IMG src=\"InterfacePictures11482_crystals_mini_north\"/><BR/> <P align=\"right\">北部水晶塔<BR/><BR/> 可以接受<BR/><BR/> 黄色、红色<BR/><BR/> 以及绿色的<BR/><BR/> 水晶。</P><BR/> <BR/><BR/> <BR/><BR/> <BR/><BR/> <BR/><BR/> <P align=\"left\">水晶塔依靠黄色水晶作为主要能源来制造特殊的物品。</P><BR/> </BODY><BR/> </HTML>');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1512, '<HTML><BR/> <BODY><BR/> <H1 align=\"center\">$B 北部水晶组合$B </H1>$B <BR/><BR/> <P align=\"left\">可能产生两种效果：一种制造伤害，另一种则可以抵消伤害。</P>$B <BR/><BR/> <P align=\"left\">爆炸水晶：需要红色和黄色水晶。<BR/>它将产生大爆炸，并且令你的敌人昏迷。</P>$B <BR/><BR/> <P align=\"left\">恢复水晶：需要黄色和绿色水晶。<BR/>它将为你提供一个治疗光环。$B </P>$B </BODY><BR/> </HTML>');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1513, '<HTML><BR/> <BODY><BR/> <H1 align=\"center\"><BR/> 北部水晶塔图表<BR/> </H1><BR/> <BR/><BR/> <IMG src=\"InterfacePictures11482_crystals_north\"/><BR/> </BODY><BR/> </HTML>');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1514, '<HTML><BR/> <BODY><BR/> <H1 align=\"center\"><BR/> 第二章：东部水晶塔<BR/> </H1><BR/> <BR/><BR/> <BR/><BR/> <IMG src=\"InterfacePictures11482_crystals_mini_east\"/><BR/> <P align=\"right\">东部水晶塔<BR/><BR/> 可以接受<BR/><BR/> 蓝色、黄色<BR/><BR/> 以及绿色的<BR/><BR/> 水晶。</P><BR/> <BR/><BR/> <BR/><BR/> <BR/><BR/> <BR/><BR/> <P align=\"left\">水晶塔依靠蓝色水晶作为主要能源来制造物品。</P><BR/> </BODY><BR/> </HTML>');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1515, '<HTML><BR/> <BODY><BR/> <H1 align=\"center\"><BR/> 东部水晶组合<BR/> </H1><BR/> <BR/><BR/> <P align=\"left\">可能产生两种效果：一种提高你的精神属性，另一种则能够制造一种护盾。</P><BR/> <BR/><BR/> <P align=\"left\">精神水晶：需要蓝色和绿色水晶。<BR/>它可以提高你的精神属性。</P><BR/> <BR/><BR/> <P align=\"left\">尖刺水晶：需要蓝色和黄色水晶。<BR/>它可以为你提供一层能对攻击你的敌人造成伤害的护盾。<BR/> </P><BR/> </BODY><BR/> </HTML>');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1516, '<HTML><BR/> <BODY><BR/> <H1 align=\"center\"><BR/> 东部水晶塔图表<BR/> </H1><BR/> <BR/><BR/> <IMG src=\"InterfacePictures11482_crystals_east\"/><BR/> </BODY><BR/> </HTML>');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1530, '<HTML>\n<BODY>\n<H1 align=\"center\">\n东塔台图\n</H1>\n<BR/>\n<IMG src=\"Interface\\Pictures\\11482_crystals_east\"/>\n</BODY>\n</HTML>');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1531, '<HTML><BR/> <BODY><BR/> <H1 align=\"center\"><BR/> 第三章：西部水晶塔<BR/> </H1><BR/> <BR/><BR/> <BR/><BR/> <IMG src=\"InterfacePictures11482_crystals_mini_west\"/><BR/> <P align=\"right\">西部水晶塔<BR/><BR/> 可以接受<BR/><BR/> 红色、绿色<BR/><BR/> 以及蓝色<BR/><BR/> 水晶。</P><BR/> <BR/><BR/> <BR/><BR/> <BR/><BR/> <BR/><BR/> <P align=\"left\">水晶塔依靠红色水晶作为主要能源来制造特殊的物品。</P><BR/> </BODY><B');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1532, '<HTML><BR/> <BODY><BR/> <H1 align=\"center\"><BR/> 西部水晶组合<BR/> </H1><BR/> <BR/><BR/> <P align=\"left\">可能产生两种效果：一种可以增强你抵抗伤害的能力，另一种则能降低敌人的防御力。</P><BR/> <BR/><BR/> <P align=\"left\">防御水晶：需要红色和绿色水晶。<BR/>它将提高你的护甲值。</P><BR/> <BR/><BR/> <P align=\"left\">破甲水晶：需要蓝色和红色水晶。<BR/>它将降低指定目标的护甲值。<BR/> </P><BR/> </BODY><BR/> </HTML>');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1533, '<HTML><BR/> <BODY><BR/> <H1 align=\"center\"><BR/> 西部水晶塔图表<BR/> </H1><BR/> <BR/><BR/> <IMG src=\"InterfacePictures11482_crystals_west\"/><BR/> </BODY><BR/> </HTML>');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1550, '<HTML>$B<BODY>$B<H1 align=\"center\">$B西部水晶塔图表$B</H1>$B<BR/>$B<IMG src=\"InterfacePictures11482_crystals_west\"/>$B</BODY>$B</HTML>');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1551, '你好，冒险者，我向你致敬!$B$B 我，克林科·古德斯迪尔，需要一些人帮我完成一些艰钜的任务。因为我自己有很多工和工作，因此得由一个足够勇敢的冒险者来找到清单里列出的物品，并把它们交给我，我保证支付丰厚的报酬。$B$B 清单将在下几页中列出……');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1552, '水晶蜘蛛腿 -$B$B 灼热峡谷中生活着许多晶网蜘蛛，它们的身体是由坚固的水晶构成的。如果你在杀死它们的时候没有打断它们的腿，就可以帮我的伙伴收集这种在工程学中必不可少的传导材料。$B$B 我需要一定数量的水晶蜘蛛腿来进行实验，对于一名坚定的战士或者训练有素的法师来说，从八条腿的蜘蛛身上找到一条完整的腿似乎不是什么问题。');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1553, '延误的包裹 -$B$B 我雇佣了一个我原以为足够可靠的飞行员，让他帮我从塞拉摩带一个包裹过来。显然我错了。虽然那个飞行员活了下来，但他是在放弃了自己的飞艇和上面所有的货物之后才保住自己性命的……$B$B 好吧，重点是，我要我的包裹。根据比吉尔的描述，他的飞艇就坠毁在塞拉摩海港西边的尘泥沼泽里，是在翻过水边的小山时开始坠落的。$B$B 嗯……我根本不知道他的飞艇是怎么坠毁的……');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1554, '古德斯迪尔的长剑 -$B$B 最后，我要找回的是我最好的武器。我将这把神奇的长剑给了一名我非常尊重的战士。嗯，不过他被一个名叫加奎亚还是什么的鱼人给袭击了。$B$B 当他在悲伤沼泽那一带的海边旅行的时候遭到了那个家伙的攻击。他被打得落荒而逃，但是他没有把那把武器也一起带走，而是留给了那个鱼人。');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1555, '古德斯迪尔的长剑 -$B$B 最后，我要找回的是我最好的武器。我将这把神奇的长剑给了一名我非常尊重的战士。嗯，不过他被一个名叫加奎亚还是什么的鱼人给袭击了。$B$B 当他在悲伤沼泽那一带的海边旅行的时候遭到了那个家伙的攻击。他被打得落荒而逃，但是他没有把那把武器也一起带走，而是留给了那个鱼人。');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1556, '帮我找到这些东西，然后带到加基森来——别忘了还有这个帐本，我会重赏你的。');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1590, '帮我找到这些东西，然后带到加基森来——别忘了还有这个帐本，我会重赏你的。');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1591, '这块铁砧是乔丹·斯迪威尔的财产 - 请不要使用!$B$B - 乔丹');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1610, '这块铁砧是乔丹·斯迪威尔的财产 - 请不要使用!$B$B - 乔丹');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1611, '<这个页面上写满了古代精灵文字。>$B$B 这些书页中记述了在制作次级秘药的过程中所发生的事件。$B$B 愿我的敌人永远无法得到这些资料。$B$B 愿我有生之年能够重见洁白的月光照耀奎尔萨拉斯。$B$B 愿我能以凯尔萨斯之名奋战。$B$B 愿我能为伊利丹的荣耀而战死。$B$B - 卡里尔·温萨鲁斯');
INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (1630, '<这个页面上写满了古代精灵文字。>$B$B 这些书页中记述了在制作次级秘药的过程中所发生的事件。$B$B 愿我的敌人永远无法得到这些资料。$B$B 愿我有生之年能够重见洁白的月光照耀奎尔萨拉斯。$B$B 愿我能以凯尔萨斯之名奋战。$B$B 愿我能为伊利丹的荣耀而战死。$B$B - 卡里尔·温萨鲁斯');
