INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (3192, '姓名：$N$B状态：生存*$B评价：消灭了数百名血色十字军成员。还渴望更多杀戮。$B$B<接下去还有数百页。>');
