INSERT INTO `locales_page_text` (`entry`, `Text_loc4`) VALUES (3196, '姓名：考巴尔特$B状态：生存*$B备注：这名死亡骑士的各部分躯体终于被找到了。大部分被用来缝合为憎恶，首级和脑子拿去烧了。');
